mapper.select:
select 

from comment
where c_id = #{cId,jdbcType=INTEGER}
  and p_id = #{pId,jdbcType=INTEGER}

select

from comment
where p_id = #{pId,jdbcType=INTEGER}
and open_id = #{openId,jdbcType=VARCHAR}

mapper.delete:
delete from comment
where c_id = #{cId,jdbcType=INTEGER}
  and p_id = #{pId,jdbcType=INTEGER}

delete from comment
where c_id = #{cId,jdbcType=INTEGER}
  and open_id = #{openId,jdbcType=VARCHAR}

mapper.update:
update comment


    c_avatar = #{cAvatar,jdbcType=VARCHAR},


    c_content = #{cContent,jdbcType=VARCHAR},


    open_id = #{openId,jdbcType=VARCHAR},


where c_id = #{cId,jdbcType=INTEGER}
  and p_id = #{pId,jdbcType=INTEGER}

update comment
set c_avatar = #{cAvatar,jdbcType=VARCHAR},
  c_content = #{cContent,jdbcType=VARCHAR},
  open_id = #{openId,jdbcType=VARCHAR}
where c_id = #{cId,jdbcType=INTEGER}
  and p_id = #{pId,jdbcType=INTEGER}
