mapper.select:
select

from post
where open_id = #{openId, jdbcType=VARCHAR}
and p_id = #{pId, jdbcType=INTEGER}

mapper.delete:
delete from post
where p_id = #{pId,jdbcType=INTEGER}
  and open_id = #{openId,jdbcType=VARCHAR}

mapper.insert:
insert into post (p_id, open_id, p_avatar, 
  nick_name, time_stamp, p_content, 
  image, location, favoriteNum
  )
values (#{pId,jdbcType=INTEGER}, #{openId,jdbcType=VARCHAR}, #{pAvatar,jdbcType=VARCHAR}, 
  #{nickName,jdbcType=VARCHAR}, #{timeStamp,jdbcType=TIMESTAMP}, #{pContent,jdbcType=VARCHAR}, 
  #{image,jdbcType=VARCHAR}, #{location,jdbcType=VARCHAR}, #{favoritenum,jdbcType=INTEGER}
  )

mapper.update:
update post
set p_avatar = #{pAvatar,jdbcType=VARCHAR},
  nick_name = #{nickName,jdbcType=VARCHAR},
  time_stamp = #{timeStamp,jdbcType=TIMESTAMP},
  p_content = #{pContent,jdbcType=VARCHAR},
  image = #{image,jdbcType=VARCHAR},
  location = #{location,jdbcType=VARCHAR},
  favoriteNum = #{favoritenum,jdbcType=INTEGER}
where p_id = #{pId,jdbcType=INTEGER}
  and open_id = #{openId,jdbcType=VARCHAR}

update post
set favoriteNum = #{num, jdbcType=INTEGER} + favoriteNum
where nick_name = #{nickName, jdbcType=VARCHAR}
 and p_id = #{pId, jdbcType=INTEGER}
